import React from "react";
import { Editor, EditorState, Modifier, RichUtils } from "draft-js";

function MyEditor() {
  const [editorState, setEditorState] = React.useState(() => EditorState.createEmpty());

const contentState = editorState.getCurrentContent();
const contentStateWithEntity =contentState.createEntity(
    'LINK', // The entity type (e.g., 'LINK', 'IMAGE', etc.)
  'MUTABLE', // The mutability ('MUTABLE', 'IMMUTABLE', or 'SEGMENTED')
  { url: 'https://example.com' } // The metadata to associate with this entity
);

const entityKey = contentStateWithEntity.getLastCreatedEntityKey();

//Applying the Entity to Text

const selectionState = editorState.getSelection();
const contentStateWithLink = Modifier.applyEntity(
    contentStateWithEntity, // The ContentState containing the entity
    selectionState,         // The selected range of text
    entityKey               // The key of the entity to apply
);

const newEditorState = EditorState.push(
    editorState,

);

  // Handle Key Commands
  const handleKeyCommand = (command, editorState) => {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      setEditorState(newState);
      return "handled"; // Correct return value
    }
    return "not-handled"; // Correct return value
  };

  // Toggle Bold Style
  const onBoldClick = () => {
    setEditorState(RichUtils.toggleInlineStyle(editorState, "BOLD"));
  };

  return (
    <div style={{ border: "1px solid #ccc", padding: "10px", minHeight: "200px" }}>
      <button onClick={onBoldClick} style={{ marginBottom: "10px", cursor: "pointer" }}>
        Bold
      </button>
      <Editor
        editorState={editorState}
        onChange={setEditorState}
        handleKeyCommand={handleKeyCommand}
      />
    </div>
  );
}

export default MyEditor;
